INSERT INTO `wp8g_comments` (`comment_ID`, `comment_post_ID`, `comment_author`, `comment_author_email`, `comment_author_url`, `comment_author_IP`, `comment_date`, `comment_date_gmt`, `comment_content`, `comment_karma`, `comment_approved`, `comment_agent`, `comment_type`, `comment_parent`, `user_id`) VALUES
(1, 1, 'A WordPress Commenter', 'wapuu@wordpress.example', 'https://wordpress.org/', '', '2016-12-13 20:17:16', '2016-12-13 20:17:16', 'Hi, this is a comment.\nTo get started with moderating, editing, and deleting comments, please visit the Comments screen in the dashboard.\nCommenter avatars come from <a href=\"https://gravatar.com\">Gravatar</a>.', 0, '1', '', '', 0, 0);
INSERT INTO `wp8g_ihc_dashboard_notifications` (`type`, `value`) VALUES
('users', 1),
('orders', 0);
INSERT INTO `wp8g_ihc_notifications` (`id`, `notification_type`, `level_id`, `subject`, `message`, `status`) VALUES
(1, 'email_check', '-1', '{blogname}: Email Verification', '<p>Hi {first_name} {last_name},</p><br/>\n\n<p>You must confirm/validate your Email Account before logging in.</p><br/>\n\n<p>Please click on the following link to successfully activate your account:<br/>\n<a href=\"{verify_email_address_link}\">click here</a></p><br/>\n\n<p>Have a nice day!</p><br/>', 1),
(2, 'email_check_success', '-1', '{blogname}: Email Verification Successfully', '<p>Hi {first_name} {last_name},</p><br/>\n\n<p>Your account is now verified at {blogname}.</p><br/>\n\n<p>Have a nice day!</p><br/>', 1),
(3, 'reset_password', '-1', '{blogname}: Reset Password request', '<p>Hi {first_name} {last_name},</p></br>\n\n<p>You or someone else has requested to change password for your account: {username}</p></br>\n\n<p>Your new Password is: <strong>{NEW_PASSWORD}</strong></p></br>\n\n<p>To update your Password once you are logged from your Profile Page:\n<a href=\"{account_page}\">{account_page}</a></p></br>\n\n<p>If you did not request for a new password, please ignore this Email notification.</p>', 1),
(4, 'admin_user_register', '-1', '{blogname}: New Membership User registration', '<html><head></head><body><p>New Membership User registration on: <strong> {blogname} </strong></p>\n					\n<p><strong> Username:</strong> {username}</p>\n\n<p><strong> Email:</strong> {user_email}</p>\n\n<p><strong> Level Name:</strong> {level_name}</p>\n\n<p>Have a nice day!</p>\n					</body></html>', 1),
(5, 'reset_password_process', '-1', '{blogname}: Reset Password request', '<p>Hi {first_name} {last_name},</p></br>\n	\n<p>You or someone else has requested to change password for your account: {username}</p></br>\n	\n<p>To confirm this request click <a href=\"{password_reset_link}\">here</a></p></br>\n\n<p>A new generated Password will be sent via Email next after the request was confirmed.</p>	\n\n<p>If you did not request for a new password, please ignore this Email notification.</p>', 1),
(6, 'change_password', '-1', '{blogname}: Your Password has been changed', '<p>Hi {first_name} {last_name},</p><br/>\n				\n<p>Your Password has been changed.</p><br/>\n\n<p>To login please fill out your credentials on:<br/>\n<a href=\"{login_page}\">{login_page}</a></p><br/>\n\n<p>Your Username: {username}</p><br/>\n\n<p>Have a nice day!</p>', 1),
(7, 'register', '-1', '{blogname}: Welcome to {blogname}', '<p>Hi {username},</p><br/>\n\n<p>Thanks for registering on {blogname}. Your account is now active.</p><br/>\n\n<p>To login please fill out your credentials on:<br/>\n{login_page}</p><br/>\n\n<p>Your Username: {username}</p><br/><br/>\n\n\n<p>Have a nice day!</p>', 1),
(8, 'review_request', '-1', '{blogname}: Welcome to {blogname}', '<p>Hi {username},</p><br/>\n\n<p>Thanks for registering on {blogname}. Your account is waiting to be approved.</p><br/>\n\n<p>Once your Account is approved you can login using your credentials on:<br/>\n<a href=\"{login_page}\">{login_page}</a></p><br/>\n\n<p>Your Username: {username}</p><br/><br/>\n\n\n<p>Have a nice day!</p>', 1),
(9, 'approve_account', '-1', '{blogname}: Your Account has been activated', '<p>Hi {username},</p><br/>\n					\n<p>Your Account has been activated!</p><br/>\n				\n<p>Have a nice day!</p>', 1),
(10, 'bank_transfer', '-1', '{blogname}: Payment Inform', 'Hi {username},\n\nPlease proceed the bank transfer payment for: {currency}{amount}\n\n<strong>Payment Details:</strong> Subscription {level_name} for {username} with Identification: {user_id}_{level_id}\n\n&nbsp;\n\n<strong>Bank Details:</strong>\n\nIBAN:xxxxxxxxxxxxxxxxxxxx\n\nBank NAME', 1),
(11, 'register_lite_send_pass_to_user', '-1', '{blogname}: Your Password', '<html><head></head><body>\n<p>Hi {username}</p>\n<p>Your password for {blogname} is {NEW_PASSWORD}</p>\n					</body></html>', 1);
